const secret = "test-secret";

function signToken(userId: string) {
  return jwt.sign(
    {
      sub: userId,
      preferred_username: userId,
    },
    secret,
    {
      algorithm: "HS256",
      issuer: "test-issuer",
      audience: "test-audience",
    },
  );
}

type TableMessage = {
  type: string;
  tableState?: {
    tableId: string;
    hand: {
      handId: string;
      currentStreet: string;
      communityCards: string[];
      currentTurnSeat: number;
    } | null;
  };
};

async function waitForMessage(ws: WebSocket, predicate: (message: TableMessage) => boolean) {
  return new Promise<TableMessage>((resolve, reject) => {
    ws.on("message", (data) => {
      const message = JSON.parse(data.toString());
      if (predicate(message)) {
        resolve(message);
      }
    });
    ws.on("error", reject);
  });
}

describe("hand flow", () => {
  it("plays a full hand through showdown", async () => {
    process.env.JWT_HS256_SECRET = secret;
    process.env.JWT_ISSUER = "test-issuer";
    process.env.JWT_AUDIENCE = "test-audience";

    resetTables();
    resetTableStates();
    const summary = createTable({
      name: "Hand Flow",
      config: {
        smallBlind: 5,
        bigBlind: 10,
        maxPlayers: 2,
        startingStack: 100,
        bettingStructure: "NoLimit",
      },
    });

    const server = createServer({ useInMemoryTelemetry: true });
    await new Promise<void>((resolve) => server.listen(0, resolve));
    const port = (server.address() as AddressInfo).port;

    const tokenA = signToken("user-a");
    const tokenB = signToken("user-b");

    await request(server)
      .post(`/api/tables/${summary.tableId}/join`)
      .set("Authorization", `Bearer ${tokenA}`)
      .send({ seatId: 0 });

    await request(server)
      .post(`/api/tables/${summary.tableId}/join`)
      .set("Authorization", `Bearer ${tokenB}`)
      .send({ seatId: 1 });

    const wsA = new WebSocket(`ws://localhost:${port}/ws?token=${tokenA}`);
    const wsB = new WebSocket(`ws://localhost:${port}/ws?token=${tokenB}`);

    await Promise.all([
      new Promise<void>((resolve) => wsA.on("open", () => resolve())),
      new Promise<void>((resolve) => wsB.on("open", () => resolve())),
    ]);

    wsA.send(JSON.stringify({ type: "SubscribeTable", tableId: summary.tableId }));
    wsB.send(JSON.stringify({ type: "SubscribeTable", tableId: summary.tableId }));

    let tableState = (await waitForMessage(wsA, (message) => message.type === "TableSnapshot"))
      .tableState;

    expect(tableState?.hand?.currentStreet).toBe("preflop");

    const sendAction = (ws: WebSocket, action: string, amount?: number) => {
      ws.send(
        JSON.stringify({
          type: "Action",
          tableId: summary.tableId,
          handId: tableState?.hand?.handId,
          action,
          amount,
        }),
      );
    };

    const playRound = async () => {
      if (!tableState?.hand) {
        throw new Error("missing hand state");
      }

      const turnSeat = tableState.hand.currentTurnSeat;
      const firstAction = tableState.hand.currentBet > 0 ? "Call" : "Check";
      if (turnSeat === 0) {
        sendAction(wsA, firstAction);
      } else {
        sendAction(wsB, firstAction);
      }

      tableState = (
        await waitForMessage(wsA, (message) => message.type === "TablePatch")
      ).tableState;

      if (!tableState?.hand) {
        return;
      }

      const nextSeat = tableState.hand.currentTurnSeat;
      if (nextSeat === 0) {
        sendAction(wsA, "Check");
      } else {
        sendAction(wsB, "Check");
      }

      tableState = (
        await waitForMessage(wsA, (message) => message.type === "TablePatch")
      ).tableState;
    };

    await playRound();
    await playRound();
    await playRound();
    await playRound();

    expect(tableState?.hand?.currentStreet).toBe("ended");
    expect(tableState?.hand?.communityCards.length).toBe(5);

    wsA.close();
    wsB.close();
    server.close();

    const spans = getInMemoryExporter()?.getFinishedSpans() ?? [];
    const lifecycleSpans = spans.filter((span) => span.name === "poker.hand.transition");
    expect(lifecycleSpans.length).toBeGreaterThan(0);
  });
});

