      tableId,
      handId: payload.handId,
      accepted: false,
      reason: "not_seated",
    });
    return;
  }

  const rate = checkWsRateLimit(client.connectionId, "action");
  if (!rate.ok) {
    send(client.socket, {
      type: "ActionResult",
      tableId,
      handId: payload.handId,
      accepted: false,
      reason: "rate_limited",
    });
    return;
  }

  const actionType = parseActionType(payload.action);
  if (!actionType) {
    send(client.socket, {
